	public static String getEntityName(String mine) {
		for (Iterator<String> iterator = UserFunctions.user_functions.iterator(); iterator
				.hasNext();) {
			String type = iterator.next();
			type = type.substring(type.indexOf(".") + 1);
			if (mine.toUpperCase().contains(type + "(")){
				return type;
			}
		}
		return mine;
	}

